import React, { useState } from 'react';
import ButtonPair from './ButtonPair';
import StorageBlock from './StorageBlock';
import './StorageNetwork.css';

interface IProps{
  resetPrev:()=>void;
  handleform:(i:any)=>void;
}

function StorageNetwork(props: IProps){

  const [currentForm,setCurrentForm]=useState<any>({
    storage:[{
    type:"SSD",
    volume:"root",
    capacity:50,
    encrypted:false,
    iops:100,
    backup:false,
    remarks:""}],
    network:-1});

  function handleform(){
    props.handleform(currentForm);
  }

  function deletestorage(event:any){
      let list=[...currentForm.storage]
      list=list.slice(event.target.value,1);
      setCurrentForm({...currentForm,storage:list})
  }

  return(
   <div>
     {
     <div className="postion">
     <StorageBlock form={currentForm} index={0} setCurrentForm={setCurrentForm} />
     <div>
       <button className="cross-btn" value="0" onClick={deletestorage}>X</button>
     </div>
     </div>
      }
     <div>
       <button className="volume">Add Volume</button>
     </div> 
     <div> 
       <h1 className="heading-network">Network Bandwidth Configuration</h1>
       <p className="data">Outbounded Traffic</p>
       <div className="slidecontainer">
        <input type="range" min="1" max="100" value="50" className="slider" id="myRange"/>
        </div>
        <div className="network-range">
            <p>256GB</p><p>2TB</p>
        </div>
     </div>
     <div> 
       <ButtonPair firsttext="Back" firstfunction={props.resetPrev} secondtext="Proceed" secondfunction={handleform} />
     </div>
    </div> 
  )
  } 

export default StorageNetwork;
